// --- HANDLERS ---
void handleRoot() {
  server.send(200, "text/html", index_html);
}

void handlePump() {
  if (server.hasArg("level")) {
    int levelIndex = server.arg("level").toInt();
    if (levelIndex >= 0 && levelIndex < 5) {
      currentPumpLevel = levelIndex;
      analogWrite(PUMP_PIN, PWM_LEVELS[levelIndex]);
    }
  }
  server.send(200, "text/plain", "OK");
}

void handleSmoke() {
  smokeState = (server.arg("state") == "on");
  digitalWrite(SMOKE_PIN, smokeState ? HIGH : LOW);
  server.send(200, "text/plain", "OK");
}

void handleLights() {
  lightsState = (server.arg("state") == "on");
  digitalWrite(LIGHTS_PIN, lightsState ? HIGH : LOW);
  server.send(200, "text/plain", "OK");
}

void setup() {
  Serial.begin(115200);

  pinMode(PUMP_PIN, OUTPUT);
  pinMode(SMOKE_PIN, OUTPUT);
  pinMode(LIGHTS_PIN, OUTPUT);

  analogWrite(PUMP_PIN, 0);
  digitalWrite(SMOKE_PIN, LOW);
  digitalWrite(LIGHTS_PIN, LOW);

  WiFi.softAP(ap_ssid, ap_password);
  Serial.println(WiFi.softAPIP());

  server.on("/", handleRoot);
  server.on("/pump", handlePump);
  server.on("/smoke", handleSmoke);
  server.on("/lights", handleLights);

  server.begin();
}

void loop() {
  server.handleClient();
}